package Java;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Double number1= 13.5, number2= 6.3, result;
		result=number1+number2;
		System.out.println("number1 + number2 = "+result);
		System.out.println();
		result=number1-number2;
		System.out.println("number1 - number2 = "+result);
		System.out.println();
		result=number1*number2;
		System.out.println("number1 * number2 = "+result);
		System.out.println();
		result=number1/number2;
		System.out.println("number1 / number2 = "+result);
		System.out.println();
		result=number1%number2;
		System.out.println("number1 % number2 = "+result);
		System.out.println();
	}

}
