package Java;

public class PrintNumberTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5; 
for (int i = 1; i <=n; i++) {
	int s=5;
	for (int j = 1; j <=i; j++) {
		System.out.print(s+" ");
		s--;
	}
	
	System.out.println();
}
	}

}
